mapper.select:
select * from (
	select tb.*, rownum rnum from(
		select eventNum, subject, content, he.adminIdx, he.created, endDate, adminName, hitCount
		from hotelEvent he
		join admin a on he.adminIdx=a.adminIdx
		
			
				
			
		
		order by eventNum desc
	)tb where rownum <= #{end}
) where rnum >= #{start}

select * from(
	select tb.*, rownum rnum from (
		select replyNum, eventNum, content, her.userIdx, userName, created 
			from hoteleventreply her
			join tb_user u on her.userIdx = u.userIdx
			where eventNum=#{eventNum}
			order by replyNum desc
	)tb where rownum <=#{end}
)where rnum >=#{start}
