mapper.select:
  SELECT * FROM (
         SELECT ROWNUM rnum, tb.* FROM (
         SELECT h.hName, h.address1, newT.hcount, newT.price, h.locCode, h.hotelCode , nf.saveFilename
         FROM hotelInfo h
         LEFT OUTER JOIN(
            select * from (
              SELECT hotelCode, hcount, min(price) as price, roomcode, rank() over(partition by hotelcode order by min(price)) g
              FROM roomInfo
              GROUP BY hotelCode, hcount, roomcode
          ) where g=1  
         )  newT ON newT.hotelCode=h.hotelCode
         LEFT OUTER JOIN(
            select roomcode, saveFilename FROM roomfile
            WHERE fileCode=(SELECT min(fileCode) FROM roomfile)
         )nf ON nf.roomCode=newT.roomCode                
          
               
               (INSTR(h.address1, #{value}) > 0)
               
          
          
          
            
            ORDER BY price desc
           
            
            ORDER BY price asc
           
          
          
          
				AND( rf.roomCode NOT IN (
                	select roomCode from roomReserInfo where 

                        (TO_DATE(TO_CHAR(checkin, 'YYYYMMDD')) >= #{checkin} AND TO_DATE(TO_CHAR(checkout, 'YYYYMMDD')) <= #{checkout}) OR
                        (TO_DATE(TO_CHAR(checkout, 'YYYYMMDD')) >= #{checkin} AND TO_DATE(TO_CHAR(checkout, 'YYYYMMDD')) <= #{checkout})

                     )
                 )	
          


            )tb WHERE ROWNUM <=#{end}
         )WHERE rnum >=#{start}

SELECT r.roomCode, rt.roomTypeCode, rt.roomName, r.hCount, r.price, r.information, r.notice, r.cancel_notice, h.hname, r.hotelCode FROM roomInfo r JOIN roomType rt ON rt.roomTypeCode =r.roomTypeCode JOIN hotelInfo h ON h.hotelCode=r.hotelCode WHERE r.hotelCode = #{hotelCode}
AND( rf.roomCode NOT IN ( select roomCode from roomReserInfo where
(TO_DATE(TO_CHAR(checkin, 'YYYYMMDD')) >= #{checkin} AND TO_DATE(TO_CHAR(checkout, 'YYYYMMDD')) <= #{checkout}) OR (TO_DATE(TO_CHAR(checkout, 'YYYYMMDD')) >= #{checkin} AND TO_DATE(TO_CHAR(checkout, 'YYYYMMDD')) <= #{checkout})
) )

		SELECT * FROM (
            SELECT ROWNUM rnum, tb.* FROM (
         SELECT h.hotelCode, h.content, h.star, h.created, u.userName
         FROM HREVIEW h
         JOIN tb_user u ON u.userIdx=h.userIdx

	     where hotelCode=#{hotelCode}
		 ORDER BY created DESC

		      )tb WHERE ROWNUM <=#{end}
		   )WHERE rnum >=#{start}

SELECT r.roomCode, rt.roomName, r.price, r.hotelCode
            FROM roomInfo r
            JOIN roomType rt ON rt.roomTypeCode =r.roomTypeCode
            JOIN hotelInfo h ON h.hotelCode=r.hotelCode
			WHERE r.hotelCode = #{hotelCode}
			AND r.roomCode=#{roomCode}
