mapper.select:
select * from (
	select tb.*, rownum rnum from(
		select noticeNum, subject, content, hn.adminIdx,adminName, hn.created, hitCount
		from hotelNotice hn
		join admin a on hn.adminIdx=a.adminIdx
		
			
				
			
		
		order by noticeNum desc
	)tb where rownum <= #{end}
) where rnum >= #{start}
