SELECT * FROM ( SELECT ROWNUM rnum, tb.* FROM ( SELECT partyCode, subject, content, TO_CHAR(startDate, 'YYYY-MM-DD') startDate, TO_CHAR(endDate, 'YYYY-MM-DD') endDate, p.userIdx, u.roleCode, u.userId, u.userName, TO_CHAR(created, 'YYYY-MM-DD') created, max, enabled, confirmCode FROM party p JOIN tb_user u ON p.userIdx = u.userIdx
AND (enabled = 0)
ORDER BY partyCode DESC
) tb WHERE ROWNUM <= #{end} ) WHERE rnum >= #{start}

SELECT * FROM (
    SELECT ROWNUM rnum, tb.* FROM (
        SELECT pp.partyCode, memo, u.userIdx, u.userId, u.userName, 
            TO_CHAR(pp.created, 'YYYY-MM-DD') created
        FROM party p
        JOIN partyPeople pp
        ON p.partyCode = pp.partyCode
        JOIN tb_user u
        ON pp.userIdx = u.userIdx
        WHERE pp.partyCode = #{partyCode}
    ) tb

    WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}
