mapper.select:
SELECT NVL(COUNT(*), 0)
FROM ticket t
JOIN store s ON t.storeCode=s.storeCode

	
		locCode=#{regionCode}
	
	
		AND s.categoryCode=#{cateCode}

		SELECT * FROM (
		    SELECT ROWNUM rnum, tb.* FROM (
		    	SELECT t.ticketCode, ticketName, t.storeCode, detail.price, locCode, s.categoryCode, s.address1
		    	FROM ticket t
		    	JOIN (
		    			select ticketCode, min(price) as price
		    			FROM ticketDetail
		    			GROUP BY ticketCode
				) detail on detail.ticketCode=t.ticketCode
				JOIN store s ON t.storeCode=s.storeCode
				
					
						locCode=#{regionCode}
					
					
						AND s.categoryCode=#{cateCode}
					
				
				ORDER BY t.ticketCode DESC
				
	        )tb WHERE ROWNUM <= #{end}
       )WHERE rnum >= #{start}

SELECT storeCode, storeName, address1, address2, tel, notice, name, email, cancel_notice
FROM store
WHERE storeCode=#{storeCode}

	SELECT * FROM (
	    SELECT ROWNUM rnum, tb.* FROM (
			SELECT reviewCode, storeCode, star, content, TO_CHAR(created, 'YYYY-MM-DD') as created, u.userName
			FROM storeReview s
			JOIN tb_user u ON s.useridx = u.userIdx
			WHERE storeCode = #{storeCode}
			ORDER BY reviewCode DESC
				
	        )tb WHERE ROWNUM <= #{end}
       )WHERE rnum >= #{start}

SELECT NVL(COUNT(*), 0)
FROM storeReview
WHERE storeCode=#{storeCode}
