mapper.select:
select * from (
	select tb.*, rownum rnum from(
		select noticeNum, subject, content, sn.adminIdx, adminName, sn.created, hitCount
		from storeNotice sn
		join admin a on sn.adminIdx=a.adminIdx
		
			
				
			
		
		order by noticeNum desc
	)tb where rownum <= #{end}
) where rnum >= #{start}
