mapper.select:
select NVL(count(*),0) from workspace

	
		
	
	
		and locCode=#{locCode}
	
	and userIdx = #{userIdx}

select NVL(count(*),0) from workspace w
join tb_user u on w.userIdx = u.userIdx

	
		
	
	
		and locCode=#{locCode}
	
	
			and pay=#{routeSort}

select * from (
	select tb.*, rownum rnum from (
		select c.locName, w.workCode, to_char(w.created,'YYYY-MM-DD') created, subject, dayCount, to_char(startDay,'YYYY-MM-DD') startDay, to_char(endDay,'YYYY-MM-DD') endDay, w.locCode locCode, saveFilename, userIdx from workspace w
			join location l on w.locCode = l.locCode
			join locationCode c on l.locCode=c.locCode
				
					
						
					
					
						and w.locCode=#{locCode}
					
					and userIdx = #{userIdx}
				
				order by workCode desc
	)tb where rownum <= #{end}
) where rnum >=#{start}

select * from (
	select tb.*, rownum rnum from (
		select c.locName, w.workCode, to_char(w.created,'YYYY-MM-DD') created, subject, dayCount, to_char(startDay,'YYYY-MM-DD') startDay, to_char(endDay,'YYYY-MM-DD') endDay, w.locCode locCode, saveFilename, u.userIdx, userName,pay from workspace w
			join location l on w.locCode = l.locCode
			join locationCode c on l.locCode=c.locCode
			join tb_user u on w.userIdx = u.userIdx
				
					
						
					
					
						and w.locCode=#{locCode}
					
					
						and pay=#{routeSort}
					
				
				order by workCode desc
	)tb where rownum <= #{end}
) where rnum >=#{start}
